function sayHello(){$.ajax({url:"/users/hello",method:"get",success:function(a,b,c){getUserName(a)},error:function(a,b,c){console.log(b,c)},complete:function(a,b){console.log("getData() Ajax Get Complete:",b)}})}function getUserName(a){$.ajax({url:"/users/hello/"+a,method:"get",success:function(a){displayGreeting(a)},error:function(a){console.log(a)}})}function displayGreeting(a){$(".welcome").text("oh. hi again, "+a)}function showUsers(){$.ajax({url:"/users/show",method:"get",success:function(a,b,c){processUsers(a)},error:function(a,b,c){console.log(b,c)},complete:function(a,b){console.log("getData() Ajax Get Complete:",b)}})}function processUsers(a){for(var b=0;b<a.length;b++){var c=a[b];first_name=c.name.first,last_name=c.name.last,username=c.username,appendName(username,first_name,last_name)}}function appendName(a,b,c){var d="<td>"+a+"</td>",e="<td>"+b+" "+c+"</td>";$(".table").append("<tr>"+d+e+"</tr>")}$(document).ready(function(){sayHello(),showUsers()});